import React, { useState, useEffect } from 'react';
import { connect } from 'react-redux';
import {
    refresh
} from '../actions/auth';
import {
    get_shipping_options
} from '../actions/shipping';
import {
    get_payment_total,
    get_client_token,
    process_payment
} from '../actions/payment';
import CartItem from '../components/CartItem';

const Checkout = ({
    items,
    total_items,
    refresh,
    get_shipping_options,
    shipping,
    get_payment_total,
    get_client_token,
    process_payment,
    isAuthenticated,
    user,
    clientToken,
    made_payment,
    loading,
    original_price,
    total_amount,
    total_compare_amount,
    estimated_tax,
    shipping_cost
}) => {
    const [formData, setFormData] = useState({
        shipping_id: 0,
    });

    const { shipping_id } = formData;

    const onChange = e => setFormData({ ...formData, [e.target.name]: e.target.value });

    const onSubmit = e => {
        e.preventDefault();
    };

    useEffect(() => {
        window.scrollTo(0, 0);

        refresh();
        get_shipping_options();
    }, []);

    useEffect(() => {
        get_client_token();
    }, [user]);

    useEffect(() => {
        get_payment_total(shipping_id);
    }, [shipping_id]);


    const showItems = () => {
        return (
            <div>
                <h4 className='text-muted mt-3'>
                    Your cart has {total_items} item(s)
                </h4>
                <hr />
                {
                    items && 
                    items !== null && 
                    items !== undefined && 
                    items.length !== 0 && 
                    items.map((item, index) => {
                        let count = item.count;

                        return (
                            <div key={index}>
                                <CartItem
                                    item={item}
                                    count={count}
                                    showViewProduct={false}
                                    showRemoveProduct={false}
                                    showUpdateProduct={false}
                                    showQuantity
                                />
                            </div>
                        );
                    })
                }
            </div>
        );
    };

    const renderShipping = () => {
        if (shipping && shipping !== null && shipping !== undefined) {
            return (
                <div className='mb-5'>
                    {
                        shipping.map((shipping_option, index) => (
                            <div key={index} className='form-check'>
                                <input
                                    className='form-check-input'
                                    onChange={e => onChange(e)}
                                    value={shipping_option.id}
                                    name='shipping_id'
                                    type='radio'
                                    required
                                />
                                <label className='form-check-label'>
                                    {shipping_option.name} - ${shipping_option.price} ({shipping_option.time_to_delivery})
                                </label>
                            </div>
                        ))
                    }
                </div>
            );
        }
    };


    return (
        <div className='container mt-5 mb-5'>
            <div className='row'>
                <div className='col-7'>
                    {showItems()}
                </div>
                <div className='col-5'>
                    <h2 className='mb-3'>Order Summary</h2>
                    <p>ORDER DETAILS</p>
                    <form className='mt-5' onSubmit={e => onSubmit(e)}>
                        <h4 className='text-muted mb-3'>
                            Select Shipping Option:
                        </h4>
                        {renderShipping()}
                    </form>
                </div>
            </div>
        </div>
    )
};

const mapStateToProps = state => ({
    isAuthenticated: state.auth.isAuthenticated,
    user: state.auth.user,
    items: state.cart.items,
    total_items: state.cart.total_items,
    shipping: state.shipping.shipping,
    clientToken: state.payment.clientToken,
    made_payment: state.payment.made_payment,
    loading: state.payment.loading,
    original_price: state.payment.original_price,
    total_amount: state.payment.total_amount,
    total_compare_amount: state.payment.total_compare_amount,
    estimated_tax: state.payment.estimated_tax,
    shipping_cost: state.payment.shipping_cost
});

export default connect(mapStateToProps, {
    refresh,
    get_shipping_options,
    get_payment_total,
    get_client_token,
    process_payment
})(Checkout);
